void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    // Time varying pixel color
    float r = mod(pow(fragCoord.x, 2.) + pow(fragCoord.y, 2.) + iTime * 100., 255.) / 255.;
    float g = mod(pow(fragCoord.x, 2.) + pow(fragCoord.y, 2.) + iTime * 100. + 85., 255.) / 255.;
    float b = mod(pow(fragCoord.x, 2.) + pow(fragCoord.y, 2.) + iTime * 100. + 170., 255.) / 255.;
    vec3 col = vec3(r, g, b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
