#define h(u) fract(dot(u.yxx * .1234, ceil(u.yxx) * .23 ) * R( iDate.w * .1 ) )
#define I iResolution
#define R round

float tex( vec2 u, float s ){

    vec2 t = R(u / -s);

    float r = R( h( ( t * 5. ) ) );

    u -= s * R( u / s ); // hp mod

    if( r > 0. )
      u.x *= -1.;

    if( abs(u.x + u.y) > .05 )
      u += s * ( ( u.x + u.y > 0. ) ? -.5 : .5 );

    return (length(u) - s * .5) * ( r != mod( t.x + t.y, 2. ) ? 1. : -1. );

}

void mainImage( out vec4 U, in vec2 V )
{

    U = vec4( step( tex( ( V + V - I.xy ) / I.x, clamp( R( fract(iTime*.1) * 10. ) * .1, .06, .25 ) ), 0. ) );
}
