vec3 solarSpectrum(float wavelength)
{
    vec3 v = vec3(128, 43,  255)/255.;
    vec3 b = vec3(0,   0,   255)/255.;
    vec3 c = vec3(0,   255, 255)/255.;
    vec3 g = vec3(0,   255, 0)/255.;
    vec3 y = vec3(255, 255, 0)/255.;
    vec3 o = vec3(255, 165, 0)/255.;
    vec3 r = vec3(255, 0,   0)/255.;
    vec3 black = vec3(0);

    vec3 color = mix(
        mix(
            mix(v, b, smoothstep(380., 470., wavelength)),
            mix(c, g, smoothstep(560., 630., wavelength)),
            smoothstep(430., 590., wavelength)
        ),
        mix(
            mix(y, o, smoothstep(600., 680., wavelength)),
            r,
            smoothstep(650., 720., wavelength)
        ),
        smoothstep(510., 610., wavelength)
    );

    color = mix(
        mix(black, color, smoothstep(380., 500., wavelength)),
        mix(color, black, smoothstep(690., 750., wavelength)),
        smoothstep(380., 750., wavelength)
    );
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    float wavelength = mix(380., 750., uv.x+1.*0.5);
    vec3 color = solarSpectrum(wavelength);

    fragColor = vec4(color, 1.);
}
