const float speed_constant = 6.28318;
const float M_PI = 3.1415926535897932384626433832795;

const float waves = 8.0;
const float speed = 0.65;
const float anim_length = 15.0;
const float strength = 1.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float color = sin(waves * uv.x + (fract(iTime* speed) ) * speed_constant) * strength;
    //float color = sin(waves * uv.x + (mod(iTime * speed, anim_length))/anim_length * M_PI);

    fragColor = vec4(color, 0.0, 0.0 ,1.0);
}

