    float e, N = 5.;
    for ( int i; i++ < 210; ) 
        i%7 < 1 ? W = ( R - U-U ).yx / R.y , W.x -= .05 , e = .6 : e += e, 
        Q +=  max( sin( .5*vec4( i%7 + ivec4(-1,1,3,0)) ), 0. )
            * e / exp( 54./e * abs( .75 - length( W += W + cos( 6.28*ceil( fract(1e4*sin(dot(vec3(i,iTime,iFrame),vec3(R,97)))) * N ) / N + vec2(0,11)) ) ) );  
}





