vec3 hsvToRgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float x = uv.x * 10.0;
    float y = uv.y * 10.0;

    float freqY = sin(iTime * 0.5 + y * 0.05 + sin(x * 0.1 + iTime * 0.1));

    float color = sin(iTime * 0.1 + (x + y) * 0.1);

    float hue = color * 0.1;
    float saturation = 0.9;
    float value = 1.0;

    // Calculate the distance from the center of the screen
    float distance = length(vec2(x - 0.5, y - 0.5));

    // Create a shape that dances in time
    float shape = step(sin(iTime * 0.5 + distance * 5.0), 0.5);

    // Mix the color with black to create a sense of depth
    vec3 rgb = mix(vec3(0.0), hsvToRgb(vec3(hue, saturation, value)), shape);

    // Add some noise to the shape
    rgb += 0.1 * vec3(sin(iTime * 0.1 + distance * 2.0), cos(iTime * 0.1 + distance * 2.0), sin(iTime * 0.1 + distance * 2.0 + (x + y) * 0.1));

    // Create a subtle background dance effect
    vec3 background = 0.1 * vec3(sin(iTime * 0.01 + x * 0.05), cos(iTime * 0.01 + y * 0.05), sin(iTime * 0.01 + (x + y) * 0.05));

    // Combine the shape and background effects
    fragColor = vec4(rgb + background, 1.0);
}
