void mainImage(out vec4 color, in vec2 coord) {

    vec2 uv = coord / iResolution.xy;
    color = texture(iChannel0, uv); 


}

void mainImage( out vec4 fragColor, in vec2 uv ){

    float a = floor(float(iFrame));
    float skip=2.;
    if(mod(a,skip)<=0.){
    uv.x = 2.5*(uv.x-0.5*iResolution.x)/iResolution.y;
    uv.y = 2.5*(uv.y-0.5*iResolution.y)/iResolution.y;
    vec3 col = vec3(0.);
    float pi = 3.141592;
    float p =4.;

    a = floor(float(iFrame))/(skip*40.);



    float s=-60.;
    float n=1000.;
    float b=0.3;
    for(float theta=0.; theta<=pi/2.; theta+=pi/n){

    if(sin(theta-a)>0.){
    float r1 = abs(pow(sin(theta-a),sin(theta-a)))*sin(theta+a);
    float x1 = r1*cos(theta);
    float y1 = r1*sin(theta);
    vec2 vec = vec2(x1,y1);

    col+=b*pow(2.,s*length(uv-vec))*vec3(0.1,0.1,1.);
    }
    }

    for(float theta=0.; theta<=pi/2.; theta+=pi/n){

    if(sin(theta-a)>0.){
    float r1 = -abs(pow(sin(theta-a),sin(theta-a)))*sin(theta+a);
    float x1 = r1*cos(theta);
    float y1 = r1*sin(theta);
    vec2 vec = vec2(x1,y1);

    col+=b*pow(2.,s*length(uv-vec))*vec3(0.1,0.1,1.);
    }
    }

    for(float theta=0.; theta<=pi/2.; theta+=pi/n){

    if(cos(theta-a)>0.){
    float r1 = abs(pow(cos(theta-a),cos(theta-a)))*cos(theta+a);
    float x1 = r1*cos(theta);
    float y1 = r1*sin(theta);
    vec2 vec = vec2(x1,y1);

    col+=b*pow(2.,s*length(uv-vec))*vec3(0.8,0.1,0.2);
    }
    }


    for(float theta=0.; theta<=pi/2.; theta+=pi/n){

    if(cos(theta-a)>0.){
    float r1 = -abs(pow(cos(theta-a),cos(theta-a)))*cos(theta+a);
    float x1 = r1*cos(theta);
    float y1 = r1*sin(theta);
    vec2 vec = vec2(x1,y1);

    col+=b*pow(2.,s*length(uv-vec))*vec3(0.8,0.1,0.2);
    }
    }





    fragColor = vec4(col,1.0);    

    }else{
    uv = uv / iResolution.xy;
    fragColor = texture(iChannel0, uv); 
    }



}
