void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y = 1.0 - uv.y;
    vec3 color = vec3(0.0);

    float t = iTime * 0.5;
    vec2 p = uv - vec2(0.5);
    float r = length(p);
    float a = atan(p.y, p.x) + t;

    // Create firework explosion
    float burst = exp(-r * 10.0) * (sin(15.0 * a - t * 10.0) + 1.0);

    // Set colors
    vec3 fireworkColor = vec3(1.0, 0.5, 0.2);
    color += burst * fireworkColor;

    // Background color
    color = mix(vec3(0.0, 0.0, 0.1), color, 1.0);

    fragColor = vec4(color, 1.0);
}
