#define Pi  3.1415926
vec2 CenterUV(vec2 fragCoord )//uv的居中与反拉伸https://www.shadertoy.com/view/M3cSDS
{
    return (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = CenterUV(fragCoord );
    //float rotateX = uv.x * cos(Pi/3.) - uv.y * sin(Pi/3.) - iTime* 0.3;
    float rotateX = .6*( uv.y + acos(5.*uv.x)/5. )+ iTime* 0.3;
    uv =  abs(uv);

    float stripe1 = step(mod(rotateX * 3.,1.) ,0.5);
    float stripe2 = step(mod(rotateX * 1.5,1.) ,0.5);
    vec3 col1 = mix(vec3(1.,0.,0.),vec3(1.),stripe1);
    vec3 col2 = mix(vec3(0.,0.,1.),vec3(1.),stripe1);
    vec3 col = mix(col1,col2,stripe2);

    col *= 1. - smoothstep(0.,0.25,uv.x);//横向长度
    col *= 1. - smoothstep(0.6,0.62,uv.y);//纵向长度

    if(uv.y>0.6 &&uv.y<0.7)//一些无关紧要的装饰
        col += (smoothstep(.28, .13, uv.x));
    if(uv.y>0.7 &&uv.y<0.83)
        col += (smoothstep(.25, .05, uv.x));

    fragColor = vec4(col,1.);
}
