float sdRoundedBox( in vec2 p, in vec2 b, in vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

vec3 cosPalette( float t, vec3 a, vec3 b, vec3 c, vec3 d){
    return a + b*cos( 6.28318*(c*t+d));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv =1.0 -2.0* fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    vec3 brightness = vec3(0.4);
    vec3 contrast = vec3(0.2);
    vec3 osc = vec3(0.6, 0.3, 0.9);
    vec3 phase = vec3(0.2, 0.1,0.3);

    vec2 pos1 = sin(uv);
    pos1.x -= iTime*0.2;
    pos1 = mod(pos1,1.0)-0.5;

    float shape = length(pos1)-0.5;
    shape = min(0.0,shape);
    shape *= sin(shape*20.0);

    vec2 pos2 = sin(uv*2.0); 
    pos2.x += iTime*0.2;
    pos2 = mod(pos2,2.0)-1.0;
    float shape1 = length(pos2)-0.5;

    vec2 pos3 = uv;
    pos3.x += iTime*0.1;
    pos3 = mod(pos3,0.9)-0.45;

    float box = sdRoundedBox(pos3, vec2(0.3), vec4(0.18));
    box = smoothstep(0.0,0.01,box);

    vec3 col = cosPalette(iTime*0.8 + shape, brightness, contrast, osc, phase)*0.9;
    col += cosPalette(-iTime*0.4 + shape1, brightness, -contrast, 2.0*osc, phase-0.2)*0.8;
    vec3 col1 = col+vec3(box);
    vec3 col2 = col*vec3(box);
    col = mix(col1, col2, smoothstep(0.3,0.7,sin(iTime)*0.5+0.5));


    // Output to screen
    fragColor = vec4(col,1.0);
}
