//https://iquilezles.org/articles/palettes/
vec3 palette( float t )
{
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.1,0.2,0.5);
    vec3 c = vec3(2.0,3.0,0.0);
    vec3 d = vec3(0.50, 0.20, 0.25);
    return a + b*cos( 6.28318*(c*t+d) );
}

float smin(float a ,float b ,float k)
{


   float x = max(k-abs(a-b),0.0)/k;
   return min(a,b) - x*x*k*0.25;

}

float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    vec2 c1POS = uv-vec2(cos(iTime));
    if(iMouse.z>0.0)
    {
     c1POS = 2.0*(fragCoord-iMouse.xy )/iResolution.y ;
    }


  float c1 = sdCircle(c1POS,0.25);
  // c1 = smoothstep(0.,0.25,c1);
   float c2 = sdCircle(uv+vec2(cos(iTime),0.25),0.125);
  //c2 = smoothstep(0., 0.25,c2);

     float f = smin(c1,c2,1.5);
   f=  smoothstep(0.0, .5,f);  
   vec3 col = palette(f-iTime);
   col = mix(palette(f-iTime), vec3(0.5,0.5,0.5),f);
   fragColor = vec4(col,1.0);  
}
