// Shorthand call to psrdnoise - we don't use the bells and whistles
float noise(vec2 x) {
    vec2 g;
    return psrdnoise(x, vec2(0.0), 0.0, g);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates: fit [-1,1] square centered in window
    vec2 uv = (fragCoord*2.0-iResolution.xy)/min(iResolution.x, iResolution.y);

    float tileseed = cellnoise(2.0*uv);
    float scale = 2.0 * (1.0 + fract(tileseed*17.0));
    vec2 uvs = scale*uv + 32.0*tileseed;
    float veindir = tileseed*6.28;
    vec2 veins = vec2(cos(veindir), sin(veindir));

    float n = noise(uvs);
    n += 0.5*noise(uvs*2.0+43.34);
    n += 0.25*noise(uvs*4.0+3.14);
    n += 0.125*noise(uvs*8.0+17.71);
    n += 0.0625*noise(uvs*16.0+47.11);

    float marble = 1.0-abs(sin(dot(uvs, veins) + 0.5*n));

    vec3 bulk = vec3(0.9); // Almost white
    vec3 streaks = vec3(0.5); // Gray
    vec3 gaps = vec3(0.2); // Dark gray

    vec3 col = mix(bulk, streaks, marble);
    col = mix(col, gaps, gridlines(2.0*uv, 0.01));

    fragColor = vec4(col,1.0);
}

// anti-aliased step of a vec2
vec2 aastep(float threshold, vec2 value) {
    vec2 afwidth;
    afwidth = 0.7*fwidth(value-threshold);
    return smoothstep(-afwidth, afwidth, value-threshold);
}

// Grid lines at integer boundaries
float gridlines(vec2 v, float width) {
  vec2 distxy = abs(fract(v+0.5)-0.5);
  vec2 grid = 1.0 - aastep(width, distxy);
  return max(grid.x, grid.y);
}

// A fast and mostly adequate cellnoise
float cellnoise(vec2 p) {
    p = mod(floor(p), 289.0);
    float hash = mod((p.x*34.0 + 10.0)*p.x + p.y, 289.0);
    return mod((hash*34.0 + 10.0)*hash, 289.0) / 289.0;
}

// psrdnoise (c) Stefan Gustavson and Ian McEwan,
// ver. 2021-12-02, published under the MIT license:
// https://github.com/stegu/psrdnoise/
float psrdnoise(vec2 x, vec2 period, float alpha, out vec2 gradient)
{
	vec2 uv = vec2(x.x+x.y*0.5, x.y);
	vec2 i0 = floor(uv), f0 = fract(uv);
	float cmp = step(f0.y, f0.x);
	vec2 o1 = vec2(cmp, 1.0-cmp);
	vec2 i1 = i0 + o1, i2 = i0 + 1.0;
	vec2 v0 = vec2(i0.x - i0.y*0.5, i0.y);
	vec2 v1 = vec2(v0.x + o1.x - o1.y*0.5, v0.y + o1.y);
	vec2 v2 = vec2(v0.x + 0.5, v0.y + 1.0);
	vec2 x0 = x - v0, x1 = x - v1, x2 = x - v2;
	vec3 iu, iv, xw, yw;
	if(any(greaterThan(period, vec2(0.0)))) {
		xw = vec3(v0.x, v1.x, v2.x);
		yw = vec3(v0.y, v1.y, v2.y);
		if(period.x > 0.0)
			xw = mod(vec3(v0.x, v1.x, v2.x), period.x);
		if(period.y > 0.0)
			yw = mod(vec3(v0.y, v1.y, v2.y), period.y);
		iu = floor(xw + 0.5*yw + 0.5); iv = floor(yw + 0.5);
	} else {
		iu = vec3(i0.x, i1.x, i2.x); iv = vec3(i0.y, i1.y, i2.y);
	}
	vec3 hash = mod(iu, 289.0);
	hash = mod((hash*51.0 + 2.0)*hash + iv, 289.0);
	hash = mod((hash*34.0 + 10.0)*hash, 289.0);
	vec3 psi = hash*0.07482 + alpha;
	vec3 gx = cos(psi); vec3 gy = sin(psi);
	vec2 g0 = vec2(gx.x, gy.x);
	vec2 g1 = vec2(gx.y, gy.y);
	vec2 g2 = vec2(gx.z, gy.z);
	vec3 w = 0.8 - vec3(dot(x0, x0), dot(x1, x1), dot(x2, x2));
	w = max(w, 0.0); vec3 w2 = w*w; vec3 w4 = w2*w2;
	vec3 gdotx = vec3(dot(g0, x0), dot(g1, x1), dot(g2, x2));
	float n = dot(w4, gdotx);
	vec3 w3 = w2*w; vec3 dw = -8.0*w3*gdotx;
	vec2 dn0 = w4.x*g0 + dw.x*x0;
	vec2 dn1 = w4.y*g1 + dw.y*x1;
	vec2 dn2 = w4.z*g2 + dw.z*x2;
	gradient = 10.9*(dn0 + dn1 + dn2);
	return 10.9*n;
}
