void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float amplitude = 8.0;
    float phase = 10.0;
    float wavelength = 30.0;
    float verticalOffset = 3.0;
    float thickness = 0.1;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float sinwave = sin(uv.x * wavelength + (iTime * phase));

    float dist = sinwave-(uv.y*amplitude - verticalOffset);

    //dist = smoothstep(0.0,0.12, dist);
    float disttop = step(thickness/2.0, dist);
    float distbottom = 1.0-step(-thickness/2.0, dist);

    dist = 1.0 - (disttop + distbottom);

    // Output to screen
    fragColor = vec4(dist, dist, dist,1.0);
}
