// Based on a template, all info above ^

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime

vec3 palette(float t)
{
    vec3 a = vec3(.5,.5,.5);
    vec3 b = vec3(.5,.5,.5);
    vec3 c = vec3(1.,1.,1.);
    vec3 d = vec3(.0,0.33333,0.66666);

    return a+b*cos(6.28318*(c*t+d));
}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

mat3 Rot3dy(float a) {
    float s=sin(a), c=cos(a);
    return mat3(c, 0., s,
                0., 1., 0.,
                -s, 0., c);
}


vec2 N(float angle) {
    return vec2(sin(angle), cos(angle));
}

vec2 map( vec3 p, vec3 va, vec3 vb, vec3 vc, vec3 vd)
{

	float a = 0.0;
    float s = 0.0;
    float r = 0.5;
    float dm;
    for( int i=0; i<9; i++ )
	{
        vec3 v;
	    float d, t;
		d = dot(p-va,p-va);            { v=va; dm=d; t=0.0; }
        d = dot(p-vb,p-vb); if( d<dm ) { v=vb; dm=d; t=1.0; }
        d = dot(p-vc,p-vc); if( d<dm ) { v=vc; dm=d; t=2.0; }
        d = dot(p-vd,p-vd); if( d<dm ) { v=vd; dm=d; t=3.0; }
		p = v + 2.0*(p - v); r*= 2.0;
		a = t + 2.0*a; s*= 4.0;
	}
	return vec2( (sqrt(dm)-1.0)/r, a/s );
}

float GetDist(vec3 p) {
    p.xz *= Rot(T*.3);
    p.xy *= Rot(T*.3);

    float h = 2.3;
    vec3 va = vec3(  0.0,  h/2.,  0.0 );
    vec3 vb = vec3(  (sqrt(2.)/2.)*h, -h/2.,  0.0 );

    vec3 vc = Rot3dy(2.*PI/3.) * vb;
    vec3 vd = Rot3dy(-2.*PI/3.) * vb; 

    float centerY = (va.y+vb.y+vc.y+vd.y)/4.;

    va.y -= centerY;
    vb.y -= centerY;
    vc.y -= centerY;
    vd.y -= centerY;

    vec2 frac = map(p, va, vb, vc, vd);   

    float d = frac.x;
    d = mix(length(p)-(sqrt(2.)/2.)*h, d, abs(sin(T*1.5))*0.1+0.9);
    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    vec3 n = GetDist(p) - 
        vec3(GetDist(p-e.xyy), GetDist(p-e.yxy),GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 1, -3);
    ro.yz *= Rot(-m.y*PI+1.);
    ro.xz *= Rot(-m.x*TAU);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0,0), 0.8);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif);

        col *= palette(length(uv)+abs(sin(T*1.5))*0.2+0.8+1.05);

        col *= texture(iChannel0, r).rgb;
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}
