#ifdef GL_ES
precision mediump float;
#endif

vec4 ball(float size, vec3 color, vec2 position, vec2 coord) {
	return vec4(color, 1.0) * size / (distance(coord, position) + 100.0) * 0.3;
}

float sround(float x, float n) {
    float c = 1.0 - x;
	return floor(x * n * c) / (n * c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 coord = (fragCoord.xy * 2.0 - iResolution.xy) * 2.0;
	float scale = iResolution.x * 0.5;

	float s = sin(iTime) * scale;
	float c = cos(iTime) * scale;

	float size[5];
	vec2 position[5];
	vec3 color[5];

	size[0] = 3.0 * scale;
	size[1] = 4.5 * scale;
	size[2] = 2.5 * scale;
	size[3] = 2.5 * scale;
	size[4] = 1.5 * scale;

	//position[0] = iMouse.xy * 4.0 - iResolution.xy * 2.0;
    position[0] = vec2(c * 1.5,  -s * 2.2 - 1.2 * scale);
	position[1] = vec2(-c * 1.0,  s * 1.2 - 1.0 * scale);
	position[2] = vec2( c * 1.2, -s * 1.5 + 0.2 * scale);
	position[3] = vec2(-c * 0.5, -s * 0.8 + 0.5 * scale);
	position[4] = vec2( c * 0.8,  s * 1.0 - 0.5 * scale);

	color[0] = vec3(0.2, 0.6, 0.4);
	color[1] = vec3(0.3, 0.3, 0.5);
	color[2] = vec3(0.1, 0.4, 0.3);
	color[3] = vec3(0.3, 0.2, 0.6);
	color[4] = vec3(0.2, 0.3, 0.5);

	vec4 cc = vec4(1.0);

	for (int i = 0; i < 5; i ++) {
		cc -= ball(size[i], color[i], position[i], coord);
	}

    float steps = 12.0;
	fragColor = mix(vec4(sround(cc.r, steps), sround(cc.g, steps), sround(cc.b, steps), cc.a), cc, 0.8);
}
