float ang(in vec2 z) {return atan(z.y, z.x);}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec2 z = uv * 2.1;

    float s = 0.;
    s += ang(z - vec2(1,0));
    s += ang(z - vec2(-1,0));
    s += ang(z - vec2(1,1));
    s += ang(z - vec2(0,-1));
    s -= ang(z - vec2(0,1));
    s -= ang(z - vec2(1,-1));
    s -= ang(z - vec2(-1,1));
    s -= ang(z - vec2(-1,-1));
    s *= 0.1591549;
    s += 0.6 + iTime * 0.1;

    s = fract(s);
    // s = pow(s, 0.9);
    fragColor = vec4(vec3(s), 1);
}
