void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Calculate the UV coordinates
    vec2 uv = fragCoord/iResolution.xy;

    // Calculate the wave pattern
    float wave = sin(uv.x * 10.0 + iTime * 5.0) * 0.1;
    wave += sin(uv.y * 10.0 + iTime * 5.0) * 0.1;

    // Create the holographic effect
    float holo = sin(uv.x * 100.0 + iTime * 10.0) * 0.5 + 0.5;
    holo *= sin(uv.y * 100.0 + iTime * 10.0) * 0.5 + 0.5;

    // Output the final color
    fragColor = vec4(holo, holo, holo, 1.0);
}
