//#########################################################
//# Many thanks to the Author of youtube video            #
//# https://www.youtube.com/watch?v=t4XnK50ocMk           #
//# The Art of Code                                       #
//# This video gave me inspiration and initial knowledge. #
//#########################################################

#define WAVE 1

const vec3 C_RED = vec3(254, 0, 0)/255.;
const vec3 C_WHITE = vec3(1);
const vec3 C_BLACK = vec3(0);


const float RADIUS = .75;

const float C_WIDTH = .2;
const float C_HEIGHT = .4;

const float B_WIDTH = .4;
const float B_HEIGHT = .4;

const vec2 CROSS_POS = vec2(1.,.6);


vec3 paint_cross(vec2 uv, vec3 col, vec2 pos)
{
    uv -= pos;

    float wx = fwidth(uv.x)*3.;
    float wy = fwidth(uv.y)*3.;

    vec3 col0 = mix(C_RED, C_WHITE, smoothstep(0., wx, uv.x - C_WIDTH/2.));
    vec3 col1 = mix(C_RED, C_WHITE, smoothstep(0., wy, uv.y - C_HEIGHT/2.));
    col1 = max(col1, col0);

    col1 = max(col1, mix(C_WHITE, C_RED, 
        smoothstep(0.,wx,length(vec2(uv.x-C_WIDTH/.25, uv.y)) - RADIUS)));
    col1 = max(col1, mix(C_WHITE, C_RED,
        smoothstep(0., wx, length(vec2(uv.x+C_WIDTH/.25, uv.y)) - RADIUS)));
    col1 = max(col1, mix(C_WHITE, C_RED,
        smoothstep(0., wy, length(vec2(uv.x, uv.y-C_HEIGHT/.425)) - RADIUS)));
    col1 = max(col1, mix(C_WHITE, C_RED,
        smoothstep(0., wy, length(vec2(uv.x, uv.y+C_HEIGHT/.425)) - RADIUS)));

    col0 = mix(C_RED, C_WHITE, smoothstep(0., wx, uv.x - C_HEIGHT/2.));
    vec3 col2 = mix(C_RED, C_WHITE, smoothstep(0., wy, uv.y - C_WIDTH/2.));
    col2 = max(col2, col0);

    col2 = max(col2, mix(C_WHITE, C_RED, 
        smoothstep(0., wx, length(vec2(uv.x+C_HEIGHT/.425,uv.y)) - RADIUS)));
    col2 = max(col2, mix(C_WHITE, C_RED, 
        smoothstep(0., wx, length(vec2(uv.x-C_HEIGHT/.425,uv.y)) - RADIUS)));
    col2 = max(col2, mix(C_WHITE, C_RED, 
        smoothstep(0., wx, length(vec2(uv.x,uv.y+C_WIDTH/.25)) - RADIUS)));
    col2 = max(col2, mix(C_WHITE, C_RED, 
        smoothstep(0., wx, length(vec2(uv.x,uv.y-C_WIDTH/.25)) - RADIUS)));

    col = min(col1, col2);

    return col;
}

vec3 paint_flag(vec2 uv)
{
    float wx = fwidth(uv.x)*3.;
    float wy = fwidth(uv.y)*3.;

#if WAVE
    float t = uv.x*5. - 2.*iTime+ + uv.y*2.;
    uv.y += sin(t) *.05;
    uv.y *= 1.09;
    uv.x *= 1.2;
#endif

    uv = abs(uv);
    vec3 col = paint_cross(uv, C_WHITE, CROSS_POS);


    vec3 col0 = mix(C_RED, C_WHITE, smoothstep(0., wx, uv.x - B_WIDTH/2.));
    vec3 col1 = mix(C_RED, C_WHITE, smoothstep(0., wy, uv.y - C_HEIGHT/2.));
    col = min(col, min(col0, col1));

    col0 = mix(C_WHITE, C_BLACK, smoothstep(0., wx, uv.x - iResolution.x / iResolution.y));
    col1 = mix(C_WHITE, C_BLACK, smoothstep(0., wy, uv.y - 1.));
    col = min(col, min(col0, col1));

#if WAVE
    col *= .8 + cos(t)*.15;
#endif        
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1. to 1)
    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;
    uv.x *=  iResolution.x / iResolution.y;

    vec3 col = paint_flag(uv);

    fragColor = vec4(col, 1.);
}
