#define TWO_PI      6.28318530717958647693
#define Radius      0.9
#define CircleSoften 0.05
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv  = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    float circleMask = smoothstep(Radius + CircleSoften, Radius, length(uv));
    vec2 ramp = vec2(0.5, 0.5) + atan(uv.x, uv.y) / TWO_PI;

    float rampCircle = circleMask * ramp.x;
    fragColor = vec4(rampCircle, rampCircle, rampCircle,1);

}
