vec2 R; vec4 M; float T; int I;
#define Main void mainImage(out vec4 Q, vec2 U){UNIS
#define UNIS R=iResolution.xy;M=iMouse;T=iTime;I=iFrame;
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)

#define G(x) 0.3989422804/std*exp(-x*x/std/std)

float building(vec2 U) {
    U = 2.*(U-.5*R)/R.y;

    float a = atan(U.y,U.x);
    float l = length(U);

    return step(l-.1-.8*abs(sin(30.*a)),0.);

}
bool cell (vec2 u) {
    return u.x>=0.&&u.y>=0.&&u.x<1.&&u.y<1.;
}
float _12(vec2 U) {
    U = floor(U);
    return U.x+U.y*R.x;
}
vec2 _21(float i) {
    return .5+vec2(mod(i,R.x),floor(i/R.x));
}
float G2 (float w, float s) {
    return 0.15915494309*exp(-.5*w*w/s/s)/(s*s);
}
#define normaliz(v) (length(v)>0.?normalize(v):vec2(0))

Main
    float bu = building(U);
    if (bu==0.) {Q = vec4(0); return;}

    Q = A(U);
    vec4 d = D(Q.xy-.5);
    vec2 f = vec2(0,-.03/R.y);

    for (int x=-4;x<=4;x++)
    for (int y=-4;y<=4;y++) {
       vec4 b = B(Q.xy+vec2(x,y)-.5);
       for (int k = 0; k < 4; k++) if (b[k]>0.) {
           vec2 u = _21(b[k]);
           vec4 a = A(u);
           vec2 r = a.xy-Q.xy;
           float l = length(r);
           float L = length(U-u);
           if (l>0.){
               if (abs(L-l)<10.) {
                   f -= .05*r/l*(L-l);
               }
               f += (r/l*(
               -10.*smoothstep(2.5,0.,length(r))
               )+.4*(a.zw-Q.zw)*pow(dot(normaliz(a.zw-Q.zw),normalize(r)),4.)
               ) 
               *G2(length(r),4.);
           }

       }
    }
    Q.zw += f;
    Q.xy += Q.zw;

    if (Q.y<5.) Q.w += .1;
    if (Q.x<5.) Q.z += .1;
    if (R.y-Q.y<5.) Q.w -= .1;
    if (R.x-Q.x<5.) Q.z -= .1;

    if (M.z>0.) Q.zw -= (M.xy-Q.xy)/(1.+dot((M.xy-Q.xy),(M.xy-Q.xy)));

    if(I<1) {

        Q = vec4(U-.5,.5*normalize(U-.5*R));
        // if (length(U-vec2(.9)*R)<.02*R.x) Q.zw = vec2(-2.5,-1.5);
    }

}

