           vec2 u = _21(b[k]);
           vec4 a = A(u);
           if (cell(a.xy-U))
               Q[i++] = b[k];
           if (i>3) break;
       }
       if (i>3) break;
    }

    if (I<1) Q = vec4(_12(U),0,0,0);
}

