#define A(a) mat2(cos((a)*6.2832 + vec4(0, -1.5708, 1.5708, 0)))  // rotate
#define H(a) (cos(radians(vec3(0, 60, 120))+(a)*6.2832)*.5+.5)    // hue

void mainImage( out vec4 C, in vec2 U )
{
    float t = mod(3. + iTime/4., 100.),
          ss = sin(t)+1.5,        // sphere size
          ts = sin(t+3.)*.05-.08, // tube size
          d = 0., i = d, s, r, x;

    vec2 R = iResolution.xy,
         m = (iMouse.xy-.5*R)/R.y;

    vec3 o = vec3(0, 0, -30), // cam
         u = normalize(vec3(U-R/2., R.y)),
         c = vec3(.1), p;

    if (iMouse.z < 1.) m = vec2(t/9., sin(t)*.1); // move when not clicking

    mat2 h = A(m.x), // yaw
         v = A(m.y); // pitch

    for (; i++<90.;)
    {
        p = o+u*d;
        p.yz *= v;
        p.xz *= h;

        r = length(p); // radius
        p /= r*r/t; // transform
        x = t+round(p.x-t); // tile & pan x
        p.x -= x;
        p.y -= sinh(x); // graph movement
        s = min( length(p-round(p)) - pow(abs(2.-r), -2.) * ss, // spheres
                 length(p.xz-round(p.xz)) - ts ); // tubes

        if (s < 1e-3 || d > 1e3) break;
        d += s*1.4;
        c += min(s, .001/s);
    }
    c += c*H(length(u.xy)*c + t/2.)*.4; // color

    C = vec4(tanh(c*c*c*3.), 1);
}
