/* 
 Another crazy cool spiral
 14/Dec/2024
 Inspired by ronwnor: https://x.com/Pixelated_Donut
 Code suggested by Shane: https://www.shadertoy.com/user/Shane
 from this shader:
 https://www.shadertoy.com/view/XcGfRt
*/

#define PI 3.14159265359


// The pattern function based on your formula
float funPattern(float x, float y, float t) {
		float a = atan(y, x);
		float b = asin(sin(a - t + 3.0 * log(x*x + y*y)));
		float k = pow( 2.0 * PI, 1.0 - sqrt(sin(6.0*a)*sin(6.0*a)+b*b) );
    return 2.0 * b * cos(k) - sin(k) / (1.0/sin(12.0 * a));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord){

    vec2 uv = (fragCoord -.5*iResolution.xy)/iResolution.y;

    float patternValue = funPattern(uv.x, uv.y, iTime);

    // Background. Dark.
    vec3 bg = vec3(.02);

    // Foreground color. Rough fire. Needs more effort. 
    vec3 fg = .6 + .6*sin(6.2831*patternValue/12. +
                          vec3(0, 1, 2)*1.6 + 2.5);
    fg = pow(fg, vec3(2.6)); // Ramping up the contrast.

    // Smoothing factor. Derivatives work better, but something
    // like the following will do.
    //float sf = 2./iResolution.y;
    // Hardware derivative-based smoothing value. Not always reliable, 
    // but works fine here.
    float sf = fwidth(patternValue);

    // Smoothly blending according to the 2D isovalue.
    vec3 color = mix(fg, bg, smoothstep(0.0, sf, patternValue));

    // Rough gamma correction, but that's a whole 
    // other discussion. 
    fragColor = vec4(sqrt(color),1.0);
}
