//philip.bertani@gmail.com

#define P  8.
#define rot(a)  mat2(cos(a+vec4(0,11,33,0)))
float sc;
#define T 10.+mod(iTime,20.)/100.

float juliabulb(vec4 z) {

  z.xw *= rot(1.+T/5.); 
  z.xz *= rot(T);
  z.xy *= rot(T);

  vec4 julia = z  + vec4(.3,0,0,.1);
  float r, theta, phi=0. , phi2=0., dr=1., dotp;
  for(float i = 0.; i < 7.; ++i) {
    r = length(z);
    if(r > 2.) break;
    theta = acos(z.w/r);
    phi = atan(z.y, z.x); 
    phi2 = atan(z.z, z.x);
    dr = pow(r, P - 1.0) * dr * P + 1.0;
    r = pow(r, P);
    theta = theta * P; phi = phi * P; phi2 = phi2 * P;

    z = r*vec4(cos(theta),sin(theta)*cos(phi),
        sin(theta)*sin(phi)*cos(phi2),sin(theta)*sin(phi)*sin(phi2)) 
        + julia;

    float nn = .6;
    z = abs(z)*nn ;
    dr *= nn;

  }

  sc = dr;

  return  .5 * log(r) * r / dr ;
}

void mainImage0( out vec4 O, vec2 U) {       
    O *= 0.;
    vec2 R = iResolution.xy;
    vec4 D = normalize( vec4( U+U-R, .8*R.y, 1.2*R.y) ),
         p = vec4(-.1,0,-1.02,0); 
    float td=0.;
    for (float d,i ; ++i<50.; ) {
        d = juliabulb(p)/12. + 4e-5 ;
        p += d * D *8.;  
        td += d;
        O += .15*cos( log(1.+sc)/2. + vec4(1,2,3,0) ) *exp(-i/50.);

    }


    O = 1. - exp(-O*O);  
    O *= exp(-td*td*4e3);

}

//FabriceNeyret2 antialiasing trick
void mainImage(out vec4 O, vec2 U) {
    mainImage0(O,U);
    if ( fwidth(length(O)) > .001 ) {  // difference threshold between neighbor pixels
        vec4 o;
        for (int k=0; k < 9; k+= k==3?2:1 )
          { mainImage0(o,U+vec2(k%3-1,k/3-1)/3.); O += o; }
        O /= 9.;
        //O.b++; // uncomment to see where the oversampling occurs
    }
}
