//https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
vec3 hash32(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}
#define R iResolution.xy
#define ss(x) smoothstep(.0,.01,x)

vec2 V(vec2 c){
    return hash22(c)-.5;
}


vec4 VoronoiQ(vec2 U){

    vec2 C =floor(U+.5);       

    vec4 M = vec4(0,0,1e10,1);
    for(float x=-1.;x<=1.;x++)
    for(float y=-1.;y<=1.;y++){
        vec2 N= C+vec2(x,y),
        V=V(N),
        H=V+N, 
        Q= (H-U)*mat2(cos(iTime*V.x),sin(iTime*V.x),-sin(iTime*V.x),cos(iTime*V.x)),
        D=abs(Q);
        float d= max(D.x,D.y)- min(D.x,D.y)*(iMouse.z>0.?iMouse.y/R.y: .5);        

        if(d  < M.z ) {
            M.z=d ;
            M.w=length(H-U);
            M.xy=H;           
        }
    }
    return M;

}

void mainImage( out vec4 O, in vec2 I )
{

    vec2 U = (I - R*.5) /R.y*5. + vec2(cos(iTime*.2),sin(iTime*.14))*5.;

    vec3 col = vec3(0);

    vec2 C =floor(U+.5);       

    vec4 M = VoronoiQ(U) ;

    col= (0.5 + 0.5*cos(hash32(M.xy)*1.5+vec3(0,2,4)))*(1.2-M.w*.8);
    col =mix(vec3(1),col,.6+.4*ss(M.z-.02));

    O = vec4(col,1.0);
}
