#define MAX_STEPS 100
#define MAX_DIST 250.
#define SURF_DIST .001
#define TAU 6.283185
#define PI 3.141592

const float globalAmbient = 0.4; // how strong is the ambient lightning
const float globalDiffuse = 1.0; // how strong is the diffuse lightning
const float globalSpecular = 1.0; // how strong is the specular lightning
const float globalSpecularExponent = 64.0; // how focused is the shiny spot
const vec3 lightPos = vec3(0.0, 3.0, -0.5); // position of the light source
const vec3 lightColor = vec3(0.9, 0.9, 0.68); // color of the light source
const vec3 ambientColor = vec3(1.0, 1.0, 1.0); // ambient color

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float hash(vec2 p)
{
	return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);

}

float noise_smooth(vec2 n)
{
    vec2 fn = floor(n);
    vec2 sn = smoothstep(vec2(0),vec2(1),fract(n));

    float h1 = mix(hash(fn),hash(fn+vec2(1,0)),sn.x);
    float h2 = mix(hash(fn+vec2(0,1)),hash(fn+vec2(1)),sn.x);
    return mix(h1,h2,sn.y);
}

float fbm(vec2 p) {
    float h = 0.0;
    float a = 0.5;
    float f = 3.5;

    for (float i = 0.0; i<4.0; i++) {
        h += a * noise_smooth(p);
        a *= 0.5;
        p = p*f*Rot(3.1415/4.0)-h*0.4;
    }

    return h;
}

vec3 triplanar(in vec3 p, in vec3 n, in float k )
{
    // project+fetch
	vec3 x = vec3(fbm(p.yz ));
	vec3 y = vec3(fbm(p.zx ));
	vec3 z = vec3(fbm(p.xy ));

    // and blend
    vec3 m = pow( abs(n), vec3(k) );
	return (x*m.x + y*m.y + z*m.z) / (m.x + m.y + m.z);
}

vec2 GetDist(vec3 p) {    
    float d = 999.0;
    float mat = 1.0;

    float s = abs(length(p-vec3(0.0,1.0,0.0))-3.0)-0.01;

    if (s<2.0) {
    vec3 n = p/6.0+0.6;

    float t = sin(iTime);
    t += 1.0;
    t *= 0.25;

    s = max(s,triplanar(p/30.0*iTime,n,0.0).x-t);


    d = min(s,d);
    }
    return vec2(d,mat);
}

vec2 RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float mat;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        vec2 dS = GetDist(p);
        dO += dS.x;
        mat = dS.y;

        if(dO>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return vec2(dO,mat);
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    vec3 n = GetDist(p).x - 
        vec3(GetDist(p-e.xyy).x, GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);

    return normalize(n);
}

float ambientOcclusion(vec3 p, vec3 n){
    const int steps = 1;
    const float delta = 0.5;

    float a = 0.0;
    float weight = 0.75;
    float m;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta;
        a += weight*(d - GetDist(p + n*d).x);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

  vec3 blinn_phong(  vec3 position,
                           vec3 lightPosition,
                           vec3 ambientCol,
                           vec3 lightCol,
                           float ambientCoeff,
                           float diffuseCoeff,
                           float specularCoeff,
                           float specularExponent,
                           vec3 camera_pos
)
{
      vec3 normal = GetNormal(position);
      vec3 toEye = normalize(camera_pos - position);
      vec3 toLight = normalize(lightPosition - position);
      vec3 reflection = reflect(-toLight, normal);

      vec3 ambientFactor = ambientCol * ambientCoeff;
      vec3 diffuseFactor = diffuseCoeff * lightCol * max(0.0, dot(normal, toLight));
      vec3 specularFactor = lightCol * pow(max(0.0, dot(toEye, reflection)), specularExponent)
                     * specularCoeff;

    return ambientFactor + diffuseFactor + specularFactor;
}

float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<100; i++ )
    {
		float h = GetDist( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += h*.25;
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.4, 1. );
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*PI+1.);
    ro.xz *= Rot(-m.x*TAU);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 0.5);
    vec3 bg = mix(vec3(1.,0.557,0.561),vec3(1.,0.992,0.796),rd.y);
    vec3 col = bg;

    vec2 rm = RayMarch(ro, rd);
    float d = rm.x;

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        col = vec3(1.,0.557,0.561);
        col *= blinn_phong(p,lightPos,ambientColor,lightColor,globalAmbient,globalDiffuse,globalSpecular,globalSpecularExponent,ro);
        //col *= ambientOcclusion(p+n*0.1,n);
        col *= softshadow(p,lightPos,0.1,50.);
        col = mix(col,bg,1.0 - exp(-0.0004*d*d));
        col *= 1.3;
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}
