uint hash(uint x) {
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    return x ^ (x>>16);
}
void mainImage( out vec4 f, in vec2 c ) {
    vec2 uv = c/800. * 3. + iTime * vec2(0.5, 0.08);
    uint i=hash(uint(uv.x)+uint(uv.y)*1000U);    
    if ((i&1U)==0U) uv.xy=uv.yx;
    if ((i&2U)==0U) uv.x=-uv.x;
    uv=abs(fract(uv)-vec2(0.0,0.5));
    f = vec4(1.-(cos((uv.x+uv.y) * 8. * 3.141592)+0.9)*100.);
}
