void mainImage( out vec4 fragColor, in vec2 uv )
{
    uv.x = 2.*(uv.x-0.5*iResolution.x)/iResolution.y;
    uv.y = 2.*(uv.y-0.5*iResolution.y)/iResolution.y;
    vec3 col = vec3(0.);

    float a=25.2-cos(float(iFrame)/200.);

    for(float n=1.; n<=20.; n+=0.02){

    float bx=uv.x/(sin(uv.x+a)+cos(n*uv.x));
    float by=uv.y/(sin(uv.y+a)+cos(n*uv.y));

    float val=bx+by;

    float v1=pow(2.,-5.*abs(val-1.));
    float v2=pow(2.,-5.*abs(val-1.5));
    float v3=pow(2.,-5.*abs(val-2.));
    col+=0.007*vec3(v1,v2,v3);
    }

    fragColor = vec4(col,1.0);
}
