mat2 rot(float t)
{
    float s = sin(t);
    float c = cos(t);
    return mat2(c, s, -s, c);
}

vec2 transform(vec2 p)
{
    return vec2(p.x + p.y, cos(p.x - 0.5 * iTime)) * rot(p.y + iTime);
}

void mainImage(out vec4 O, in vec2 I)
{
    vec2 p = 8. * (2. * I - iResolution.xy) / iResolution.y;
    float weight = 1.;
    float val = 0.;
    for(uint i = 0u; i < 8u; i++)
    {
        p = transform(p);
        val += weight * p.y / length(p);
        weight *= 0.55;
    }
    val = 0.2 * (2.75 + val);
    vec3 col = vec3(val, val * val, val * 0.5);
    O = vec4(col, 1.);
}
