void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float mouse = iMouse.x / iResolution.x;

    // If mouse results to 0, set to 0.5
    mouse += .5 * step(mouse, .0);

    float isTimeEven = step(mod(iTime, 2.), 1.);

    float r = step(uv.x, mouse);

    // No need to calculate blue, will be on if red is off.
    float b = step(r, 0.);

    fragColor = mix(vec4(b, 0., r, 1.), vec4(r, 0., b, 1.), step(1.0, isTimeEven));

}
