#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
float hash(float n) { 
    return fract(sin(n) * 43758.5453123); 
}

float noise(float x) {
    float i = floor(x);
    float f = fract(x);
    float u = f * f * (3.0 - 2.0 * f);
    return mix(hash(i), hash(i + 1.0), u);
}
float noise2D(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    f = f * f * (3.0 - 2.0 * f);

    float a = hash(i.x + i.y * 57.0);
    float b = hash(i.x + 1.0 + i.y * 57.0);
    float c = hash(i.x + (i.y + 1.0) * 57.0);
    float d = hash(i.x + 1.0 + (i.y + 1.0) * 57.0);

    return mix(mix(a, b, f.x), mix(c, d, f.x), f.y);
}
float Xor(float a, float b){
    return a*(1.-b)+b*(1.-a);
}
float happy_star(vec2 uv, float anim)
{
    uv = abs(uv);
    vec2 pos = min(uv.xy/uv.yx, anim);
    float p = (2.0 - pos.x - pos.y);
    return (2.0+p*(p*p-1.5)) / (uv.x+uv.y);      
}
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
     vec2 uv = ( C - .5*iResolution.xy ) / iResolution.y;
    vec4 n1;vec3 q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<70.;
        O.xyz+=mix(vec3(0.1,0.5,2.),H(g*.1),.8)*10./e/8e3
    )
    {

     float c2= noise(n1.x+10.*cos(iTime*0.1));
      float c3= noise(n1.y+100.*cos(iTime*0.1));
       float c4= noise(n1.w+10.*sin(iTime*1.1));


        n1.w+=c4;
     n1.x+=c2;
     n1.y+=c3;
      n1.z+=c4;
       n1.xyz=g*d;


        n1.z-=iTime*10.5;
        a=20.;


        n1.xyz=mod(n1.xyz-a,a*2.)-a;
        s=2.;
       n1.xz*=mat2(cos(iTime*1.5),sin(iTime*1.5),-sin(iTime*1.5), cos(iTime*1.5));
        n1.yz*=mat2(cos(iTime*1.5),sin(iTime*1.5),-sin(iTime*1.5), cos(iTime*1.5));
        for(int i=0;i++<8;){

            n1.xyz=.3-abs(n1.xyz);
            n1.x<n1.z?n1.xyz=n1.wzy:n1.xyz;
            n1.z<n1.y?n1.xyz=n1.xyz:n1.xyz;
             n1.w<n1.x?n1.xyz=n1.zyz:n1.xyz;
            s*=e=1.7+sin(iTime*.01)*.1;
            n1.xyz=abs(n1.xyz)*e-
                vec3(
                    5.*3.,
                    120,
                    8.*5.
                 );
         }
         g+=e=length(n1.yzzz)/s;
    }
     uv *= 2.0 * ( cos(iTime * 2.0) -2.5); // scale
    float anim = sin(iTime * 12.0) * 0.1 + 1.0;  // anim between 0.9 - 1.1 
    O*= vec4(happy_star(uv, anim) * vec3(0.35,0.2,0.55)*2., 1.0);

}
