//Delayed Input
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
vec2 uv = fragCoord.xy / iResolution.xy;    
fragColor = mix(texture(iChannel1,uv),texture(iChannel0,uv), 0.2);
}

//combined output
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord.xy / iResolution.xy;

vec4 original_input = texture(iChannel0,uv);
vec4 delayed_input = texture(iChannel1,uv);


original_input =1.0 - original_input;


vec4 imgout = (original_input+delayed_input)/2.0;

fragColor = imgout;

}
