#define AA 1.5/min(iResolution.y,iResolution.x)

//https://www.shadertoy.com/view/DtKyDm
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);

}

vec4 texChar(float char, vec2 uv) {
    vec2 pt = uv/16.0;
    pt.x += char/16.0;
    pt.y += 12.0/16.0;
    return texture(iChannel0, pt);
}

float sdTextBox ( vec2 p, vec2 b, float char ) {
    float l = sdBox(p,b);
    vec2 pn = (p.xy / b.xy) * .5 + .5;
    float lt = (texChar(char, pn).w - 0.5);
    return max(lt,l); 
}
//===========================================


//https://iquilezles.org/articles/palettes/
vec3 palette( float t, float speed ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d*speed));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    //background
    vec3 color = palette(length(p), -iTime*0.4);

    //year font
    vec2 font_size = vec2(0.4);
    vec2 offset = vec2(font_size.x*(3.0*0.5),0.0);
    float gap = -font_size.x;

    float char2 = sdTextBox(p+offset, font_size, 2.0);
    offset.x+=gap;

    float char0 = sdTextBox(p+offset, font_size, 0.0);
    float d=min(char2,char0);

    offset.x+=gap;
    char2 = sdTextBox(p+offset, font_size, 2.0);
    d=min(d, char2);

    offset.x+=gap;
    float char4 = sdTextBox(p+offset, font_size, 4.0);
    d=min(d, char4);

    float size = 0.02;
    float year = smoothstep(size,size-AA,d);    

    // Glow effect
    float glowStrength = 0.5;
    float glowSize = 0.04;
    float glow = smoothstep(size+glowSize, size, d);
    glow = pow(glow, 6.0);

    color = mix(color, vec3(1.0), (year + glow * glowStrength));

    fragColor = vec4(color,1.0);
}
