void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int checkerSize = int(iResolution.x) / 8;
    int halfCheckerSize = checkerSize / 2;

    int checkerX = int(int(fragCoord.x) % checkerSize < halfCheckerSize);
    int checkerY = int(int(fragCoord.y) % checkerSize > halfCheckerSize);
    vec4 col = vec4(checkerX ^ checkerY);

    fragColor = col;
}
