const float maxR = 0.8;

//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);
    return vec3(1.0);
    //return a + b*cos( 6.28318*(c*t+d) );
}

vec3 colorcircle( vec2 uv, float r, float w, float offx, float offy )
{
    float d = length(uv);
    if(d < maxR)
    {
        if(d > maxR-w)
        {
            return vec3(1,1,1);
        }
    }
    if(d > maxR)
    {
        return vec3(0.0);
    }
    uv.x += offx;
    uv.y += offy;
    d = length(uv);
    if(d < r)
    {
        if(d > r-w)
        {
            return vec3(1,1,1);
        }
    }
    return vec3(0.0);
}

//https://www.shadertoy.com/view/mtyGWy
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    vec3 col = palette((-1.65*uv.y)+(uv.x)+(iTime*.4)*-1.);

    //finalColor += colorcircle(uv, 0.2, 0.01, 0.2*i, 0.2*x);
    float r = 0.2;
    float w = 0.01;

    // Circles are being repeated
    for(float i = 0.0; i < 6.0; i++)
    {
        finalColor += colorcircle(uv, r, w, 0.0, 0.0);
        float x = 0.0 + r * cos(radians(60.*i));
        float y = 0.0 + r * sin(radians(60.*i));
        for(float ii = 0.0; ii < 6.0; ii++)
        {
            finalColor += colorcircle(uv, r, w, x, y);
            float xx = x + r * cos(radians(60.*ii));
            float yy = y + r * sin(radians(60.*ii));
            for(float iii = 0.0; iii < 6.0; iii++)
            {
                finalColor += colorcircle(uv, r, w, xx, yy);
                float xxx = xx + r * cos(radians(60.*iii));
                float yyy = yy + r * sin(radians(60.*iii));
                finalColor += colorcircle(uv, r, w, xxx, yyy);
            }
        }
    }
    if(finalColor.x > 1.0)
    {
        finalColor = vec3(1,1,1);
    }
    finalColor *= col;
    fragColor = vec4(finalColor, 1.0);
}
