#define h21(a) fract(sin(a * 453.2734)) * 994.3434

float sBox(vec2 p){
    float k = 6.;
    p = pow(abs(p), vec2(k));
    return pow(dot(p, p/p), 1./k) - .5;
}

float rain(vec2 p){
    float h = 14., id = floor(p.x),
          t = iTime + h21(id) * .3;

    p.x = fract(p.x) - .5;

    p.y += sin(id + t) + fract(t) + 1.;

    p.y < .0 ? h = 1. : h;

    p.y += (h / 2. - .5) * fract(t);

    p.y = mod(p.y, h) - h / 2.;

    return sBox(p);
}


void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy; 
         u = 3. * vec2(u + u - r) / r.y;

    o = vec4(smoothstep(.04, .0, rain(u) + .03));
}
