#define r(n) mat2(cos(n + vec4(0, 11, 33, 0)))

void mainImage( out vec4 O, vec2 u ){      
    vec3 p, q, R = iResolution, cor, 
         D = vec3((u + u - R.xy) / R.y, 2);

    float i, d, esf, esf2, gyr, gyr2, t = iTime * .7;
    O = 0. * O;


    while(i++ < 50.) 
         q = p,

         q.z -= 2.9 + sin(t) * .2,
         q.x -= cos(t),
         q.xz *= r(-1. * t),   
         q.yz *= r(.29 * t),

         esf = length(q) - 1.3,
         esf2 = length(q) - 1.1,

         q *= 6.,

         gyr = dot(sin(q), cos(q.zxy)) + .8,
         gyr = max(esf * .5, abs(gyr * .04 ) * 1. + .00001),

         q -= .2 * vec3(cos(t * 7.), sin(t * 11.), sin(t * 5.)),

         gyr2 = dot(sin(q), cos(q.zxy)) + 1.3,
         gyr2 = max(gyr2 * .06, esf2 * .6),


         d = min(gyr, gyr2),

         cor = (cos(vec3(6, 4, 2) + p.z * 8.) + 1.) * .008,
         cor += gyr > gyr2 ? .2: .02,

         O.rgb +=  cor / (exp(d) + .7 * i) ,

         p += d * D;

    q -= 4. * vec3(cos(t * 3.), sin(t * 2.), sin(t * 1.));
    O +=  4. / pow(length(q), 2.);
    O.r +=  3. / pow(length(q), 3.);
}
