void mainImage(out vec4 o, vec2 u)
{
    u /= iResolution.y;
    for(float l = 3., d = 1., x, a, b, f, i = 0.; i < 1. && l > 2.; i += .1)
    {
        x = u.x / d + .4 * iTime + 71. * i;
        for(a = 0., b = .5; b > .001; b *= .5)
        {
            f = fract(x);
            a += mix(fract((x - f) * .37), fract((x - f + 1.) * .37), f) * b;
            x *= 2.;
        }
        if(u.y < d * a + i - .2) l = i + .3;
        d *= .8;
        o = vec4(.2, .3, .4, 0) * l;
    }
}

