#define PI 3.1415962
#define DO_SPIRAL 1.0

float atan2(in float y, in float x)
{
    return x == 0.0 ? sign(y)*PI/2.0 : atan(y, x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - 0.5;
    uv.x *= iResolution.x/iResolution.y;
    float pixel = length((iMouse.xy/iResolution.xy)-0.5);

    float POWER = 0.25;
    float CIRCLE_TIME = iTime / 2.0;
    float L1 = pow(length(uv + vec2(sin(CIRCLE_TIME), cos(CIRCLE_TIME))*pixel), POWER);
    float L2 = pow(length(uv + vec2(sin(CIRCLE_TIME+PI*0.666), cos(CIRCLE_TIME+PI*0.666))*pixel), POWER);
    float L3 = pow(length(uv + vec2(sin(CIRCLE_TIME+PI*0.666*2.0), cos(CIRCLE_TIME+PI*0.666*2.0))*pixel), POWER);
    //uv = -abs(uv);

    float SIZE = 10.0;
    L1 = fract(L1 * SIZE + DO_SPIRAL * atan2(uv.x, uv.y) / PI);
    L2 = fract(L2 * SIZE + DO_SPIRAL * 2.0*atan2(uv.x, uv.y) / PI);
    L3 = fract(L3 * SIZE + DO_SPIRAL * 3.0*atan2(uv.x, uv.y) / PI);

    float GROW_TIME = iTime / 5.0;
    L1 = sin(pow(fract(L1-2.0*GROW_TIME), 0.5)*PI);
    L2 = sin(pow(fract(L2-3.0*GROW_TIME), 0.5)*PI);
    L3 = sin(pow(fract(L3-5.0*GROW_TIME), 0.5)*PI);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(4.0*iTime+vec3(L1,L2,L3));
    //col += 0.5 + 0.5*cos(4.0*iTime+vec3(0,L2,0));
    //col += 0.5 + 0.5*cos(4.0*iTime+vec3(0,0,L3));
    //col /= 3.0;
    col = vec3(L1,L2,L3);

    col += vec3(L2,L3, L1)*0.3;
    col += vec3(L3, L1, L2)*0.2;
    col /= 1.5;

    col = pow(col, vec3(2.0));

    // Output to screen
    fragColor = vec4(col,1.0);
}
