#define R3D(a) mix((q-p)*dot(p,q-p), p, cos(a)) + sin(a)*cross(p,q-p)
#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(-2,-1,-2),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1)*2.;
    p.z-=2.;
    p.zx*=R2D(t);
    p=R3D(t*.5);

    for(j=0.;j++<8.;)
        p=(abs(p+p))-.04,
        d=max(min(p.z/d,p.y),p.x),
        e-=d;

}o+=vec4(exp(p*e),1)*3./i;
}
