#define PI 3.14159265359
#define TWO_PI PI * 2.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float A = 0.7;
    float B = 0.0;
    float C = 0.1;
    float D = 0.1;
    float speed = 1.0;

    float r = A + B * cos((C * uv.x + D) * TWO_PI + iTime * speed);

    A = A + 0.1;
    B = B - 0.1;
    C = C + 1.0;
    D = D - 0.1;
    speed = -2.0;

    float g = A + B * cos((C * uv.x + D) * TWO_PI + iTime * speed);

    A = A - 0.2;
    B = B - 0.0;
    C = C + 2.0;
    D = 0.0;
    speed = 2.0;

    float b = A + B * cos((C * uv.x + D) * TWO_PI + iTime * speed);

    vec3 col = vec3(r,g,b);


    // Output to screen
    fragColor = vec4(col, 1.0);
}
