#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//Rodrigues-Euler axis angle rotation
#define ROT(p,axis,t) mix(axis*dot(p,axis),p,cos(t))+sin(t)*cross(p,axis)

//formula for creating colors;
#define H(h)  (  cos(  h/3. + vec3(1,1,0)   )*.8 + .5 )

#define H2(h)  (  cos(  h*2. + vec3(0,2,1)   )*.7 + .2 )

//formula for mapping scale factor 
#define M(c)  log(1.+c)

#define R iResolution


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .7*R.y, R.y))*200.;

    float sc,dotp,totdist=0., tt=iTime, t=0.;

    for (float i=0.; i<100.; i++) {

        vec4 p = vec4( rd*totdist);

        //vec4 op = p;

        p.xyz += vec3(20.*cos(tt),0,-50.+20.*sin(tt));

        p.xz *= rot( tt/2.);

        p.yzw = p.xyz;

        sc = 1.; 

        p.zw *= rot(tt);

       // p.xw *= rot(tt*2.);

        vec4 w = p;

        for (float j=0.; j<6.; j++) {

            p = abs(p)*.6;

            dotp = max(1./dot(w,w),.03 + .02*sin(tt/1.3));
            sc *= dotp; 

            p = p * dotp - .1; 

            w = .8*log(1.-exp(-p*p)*p) - .18; 


        }

        float dist = length( 1. - p) /sc ;  //funky distance estimate
        float stepsize = dist/100. + 1e-4;     
        totdist += stepsize;                  //move the distance along rd

        //accumulate color, fading with distance and iteration count
        c +=
            ( .03 * H2(  atan( p.w, p.z )) 
           + .06 * mix( vec3(1), H(M(sc)),.95) )*  exp(-i*i*stepsize*stepsize*5e1);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
