vec4 getData(vec2 coord){
    return texelFetch(iChannel0, ivec2(coord), 0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = texture(iChannel0, uv);
    vec4 tex = texelFetch(iChannel0, ivec2(fragCoord), 0);

    vec3 avg = vec3(0.0);
    float N = 7.; // length of side of kernel = 2N + 1
    for(vec2 i = vec2(-N); ++i.x <= N;){
        for(i.y = -N; ++i.y <= N;) {
            vec4 data = getData(vec2(fragCoord + i));
            avg += data.rgb; 
        }
    }
    avg /= pow((N * 2.) + 1., 2.);
    fragColor = vec4(avg, 1.0);
    //fragColor = tex;
}
