void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    //uv = fract(uv * 2.0) - 0.5;

    float s = radians(sin(iTime/20.));

    float a = atan(uv.y / uv.x);

    float dgs = a * 180. / 3.14;

    a = smoothstep(0.1, 1., fract(dgs / s));

    a /= length(uv);

    fragColor = vec4(a, sin(a), cos(a), 1.0);
}
