#define NUM_SEGS 30

float Circle(vec2 p, vec2 c) {
    float d = length(p-c);
    return smoothstep(d, 0.0, d - 0.01); }

float Circle2(vec2 p, vec2 c) {
    float d = length(p-c);
    return 0.005/d; }

float Line(vec2 p, vec2 a, vec2 b) {
	vec2 pa = p - a, ba = b - a;
    float t = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);
    vec2 c = a+ba*t;
    float d = length(c-p);
    float l = 0.002, g = 0.002;
	return min(1.0, 0.003/d); }

vec2 Bezier(vec2 A, vec2 B, vec2 C, vec2 D, float t) {
    return mix(mix(mix(A, D, t), mix(D, C, t), t), mix(mix(D, C, t), mix(C, B, t), t), t); }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    float t = sin(iTime*0.3)*sin(iTime*0.3);
    vec2 A = vec2(-0.8, sin(iTime)*0.4);
    vec2 B = vec2(0.8, -sin(iTime)*0.4);
    vec2 C = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;
    vec2 D = vec2(-0.4, sin(iTime+2.)*0.4);
    vec2 CB = mix(C, B, t);
    vec2 AD = mix(A, D, t);
    vec2 CD = mix(D, C, t);
    vec2 ADCD = mix(AD, CD, t);
    vec2 CBCD = mix(CD, CB, t);
    vec2 ABCD = mix(ADCD, CBCD, t), P;         
    vec3 col = vec3(0.0);
    float t1 = min(0.5, sin(iTime+0.2)*sin(iTime+0.2));
    float t2 = sin(iTime)*sin(iTime);
    vec2 P2=A;

    col += vec3(1.0, 0.0, 0.0)*Circle(uv, A);
    col += vec3(1.0, 0.0, 0.0)*Circle(uv, B);
    col += vec3(1.0, 1.0, 0.0)*Circle(uv, C);
    col += (Line(uv, A, D) + Line(uv, C , B)+ Line(uv, C, D))* 0.2 * t1;
    col += vec3(1.0, 0.0, 0.0)*Circle(uv, D);
    col += vec3(0.0, 1.0, 0.0)*Circle2(uv, AD);
    col += vec3(0.0, 1.0, 0.0)*Circle2(uv, CD);
    col += vec3(0.0, 1.0, 0.0)*Circle2(uv, CB);
    col += (Line(uv, CD, AD))* 0.5 * t1;
    col += vec3(0.0, 0.0, 1.0)*Circle2(uv, ADCD);
    col += vec3(0.0, 0.0, 1.0)*Circle2(uv, CBCD);
    col += (Line(uv, CB, CD))* 0.2 * t1;
    col += (Line(uv, ADCD, CBCD))* 0.2 * t1;
    col += vec3(1.0, 1.0, 1.0)*Circle(uv, ABCD);

    for (int i = 0; i <= NUM_SEGS; i++) {
        float t = float(i) / float(NUM_SEGS);
        P = Bezier(A, B, C, D, t);
        col = max(col, Line(uv, P, P2)*t2);
        P2 = P; }
    fragColor = vec4(col, 1.0); }
