const float globalSpeed = 0.05;
const float globalSize  = 0.01;
const float timeOffset  = 321.0;

vec2 addPlanetCircularMotion(vec2 barycenter, float dist, float rotSpeed)
{
    float time = iTime + timeOffset;
    rotSpeed *= globalSpeed;
    return vec2(cos(time * rotSpeed), sin(time * rotSpeed)) * dist + barycenter;
}

float sdCircle(vec2 p, float r)
{
    return length(p) - r;
}

vec3 addSunSizeAndColor(vec2 uv, float radius, vec3 color)
{
    float value = .075 / sdCircle(uv, radius * globalSize);
    return 0.5 * value * exp(1.0 - 0.25 * value) * color;
}

vec3 addPlanetSizeAndColor(vec2 uv, float radius, vec3 color)
{
    return .05 / sdCircle(uv, radius * globalSize) * color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec3 color = vec3(0.0);

    vec2 sun     = uv;
    vec2 mercury = addPlanetCircularMotion(sun, 0.125, 4.790);
    vec2 venus   = addPlanetCircularMotion(sun, 0.250, 3.500);
    vec2 earth   = addPlanetCircularMotion(sun, 0.375, 2.980);
    vec2 mars    = addPlanetCircularMotion(sun, 0.500, 2.400);
    vec2 jupiter = addPlanetCircularMotion(sun, 0.610, 1.310);
    vec2 saturn  = addPlanetCircularMotion(sun, 0.760, 0.969);
    vec2 uranus  = addPlanetCircularMotion(sun, 0.875, 0.681);
    vec2 neptune = addPlanetCircularMotion(sun, 1.000, 0.543);

    color += addSunSizeAndColor(uv,   69.6340, vec3(0.1, 0.9, 0.5))
     + addPlanetSizeAndColor(mercury,  0.2440, vec3(0.1, 0.1, 0.1))
     + addPlanetSizeAndColor(venus,    0.6052, vec3(0.1, 0.1, 0.1))
     + addPlanetSizeAndColor(earth,    0.6371, vec3(0.2, 0.2, 0.1))
     + addPlanetSizeAndColor(mars,     0.3390, vec3(0.3, 0.1, 0.3))
     + addPlanetSizeAndColor(jupiter,  6.9911, vec3(0.1, 0.3, 0.1))
     + addPlanetSizeAndColor(saturn,   5.8232, vec3(0.1, 0.1, 0.9))
     + addPlanetSizeAndColor(uranus,   2.5362, vec3(0.1, 0.1, 0.1))
     + addPlanetSizeAndColor(neptune,  2.4622, vec3(0.9, 0.1, 0.1));

    fragColor = vec4(color, 1.0);
}
