float Sphere(vec3 p,float r) {
    return length(p) - r;
}

vec3 circle(vec2 uv) {
    float s =  Sphere(vec3(uv,0.0),0.5);
    float t = 0.02 / abs(s);

    return vec3(t);
}    

vec3 rollobj(vec2 uv,float speed) {
    float ts = iTime * speed;    
    float d = distance(vec2(sin(ts) * 0.5, cos(ts) * 0.5)  ,uv);
    float s = step(0.05 ,d);

    return vec3(s);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (gl_FragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);    

    vec3 r1 = rollobj(uv,1.);    
    vec3 c1 = circle(uv); 
    vec3 f1 = pow(c1,r1);

    vec3 r2 = rollobj(uv,2.);
    vec3 c2 = circle(uv); 
    vec3 f2 = pow(c2,r2);

    vec3 f =  pow(f1,f2);



    // Output to screen
    fragColor = vec4(vec3(f), 1.0);
}
