float Circle(vec2 uv, vec2 p, float r, float blur){

    float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);

    return c;
}

float Band(float t, float start, float end, float blur){
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);
    return step1 * step2;
}

float Rect(vec2 uv, float left, float right, float bot, float top, float blur){
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bot, top, blur);

    return band1 * band2;
}

float Smiley(vec2 uv, vec2 p, float size){
    uv -= p;
    uv /= size;

    float face = Circle(uv, vec2(0.), .4, .01);//face
    face -= Circle(uv, vec2(.13, .2), .07, .01);//left
    face -= Circle(uv, vec2(-.13, .2), .07, .01);//right eye

    float mouth = Circle(uv, vec2(0.), .3, .02); 
    mouth -= Circle(uv, vec2(.0, .1), .3, .02);

    face -= mouth;

    return face;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float t = iTime;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;



    float x = uv.x;
    float m = sin(t*2.5+x*8.)*.1;
    float y = uv.y-m;

    float mask = Rect(vec2(x, y), -.5, .5, -.1, .1, .1);
    //float mask = Smiley(uv, vec2(.0), 1.);
    col *= mask;
    // Output to screen
    fragColor = vec4(col, 1.0);
}
