#define PHASE_SCALE 2.
#define AMPLITUDE_GLOBAL_SCALE 0.002
#define AMPLIUDE_LOCAL_SCALE  0.05
#define NOISE_SCALE 1.0
#define NOISE_DISPERSION 10.
#define TARGET_COLOR vec3(0.45,0.15,0.55)
#define NOISE_INTENSITY 0.001 // low values are more intense

#define TWO_PI 3.1415926535
float rand(float n){return fract( sin(n) * 43758.5453123);}
float rand2(float n){return fract(cos(n) * 999742.235690);}

float noise(float p){
	float fl = floor(p);
    float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}

float noise2(float p){
	float fl = floor(p);
    float fc = fract(p);
	return mix(rand2(fl), rand2(fl + 1.0), fc);
}

float rect(float x){
    float d = 0.1;
    if(x < d){
       return x/d;
    }
    if(x < 1.-d){
        return 1.;
    }

    return (1.-x)/d;
}

float line3(vec2 point, float t){
    const float MIDDLE = 0.5;
    float z = noise(NOISE_DISPERSION*point.x + t*PHASE_SCALE)-noise(NOISE_DISPERSION*point.x - t*PHASE_SCALE);
    float y  = MIDDLE;
    float y2 = MIDDLE  + NOISE_SCALE*(noise2(z)-noise(z));

    float r  = length(point - vec2(point.x+NOISE_INTENSITY,y));
    float r2 = length(point - vec2(point.x-NOISE_INTENSITY,y2));
    return AMPLITUDE_GLOBAL_SCALE/(r * r2) +AMPLIUDE_LOCAL_SCALE/r;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 o = vec4(1, 1, 1, 1);
    vec2 v_uv0 = gl_FragCoord.xy / iResolution.xy;
    float a = line3(v_uv0,iTime);
    o.rgb = a * TARGET_COLOR;
    o.a   = a;

    fragColor = vec4(o.rgb*o.a,1.0);
}
