Main {
    Q.xyz = normalize(B(U).xyz)*2.-.5;

 }

#define M 5000

#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U)

#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)

#define vel(v) (v)*inversesqrt(1.+dot(v,v))

#define inside(v) ((v).x>0.&&(v).y>0.&&(v).x<R.x&&(v).y<R.y)

float ln (vec3 p, vec3 a, vec3 b) { 
    return length(p-a-(b-a)*(dot(p-a,b-a)/dot(b-a,b-a)));
}
 #define UIF (1.0 / float(0xffffffffU))
uint baseHash(uvec2 p){
    p = 1103515245U*((p >> 1U)^(p.yx));
    uint h32 = 1103515245U*((p.x)^(p.y>>3U));
    return h32^(h32 >> 16);
}
vec3 hash(vec2 x){
    uint n = baseHash(floatBitsToUint(x));
    uvec3 rz = uvec3(n, n*16807U, n*48271U);
    return vec3((rz >> 1) & uvec3(0x7fffffffU))/float(0x7fffffff);
}
#define pi 3.141592659

float ln (vec2 p, vec2 a, vec2 b) { 
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}

Main {

    Q = A(U);
    vec4 bb = B(U);
    vec2 m = vec2(0), n = Q.xy;
    for (int x = -1; x <= 1; x++)
    for (int y = -1; y <= 1; y++)
    if (x*x+y*y<=4)
    if (x!=0||y!=0) {
        vec2 v = vec2(x,y);
        vec4 q = A(U+v);
        vec4 b = B(U+v);
        if (inside(U+v))
        Q.xy -= .01*q.w*(1.+length(b.xyz-bb.xyz))*v/dot(v,v);
        m += 1./8.*(q.xy-n.xy);
    }
    Q.xy += .5*m.xy;
    if (length(Q.xy)>1.) Q.xy = normalize(Q.xy);
    if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.)Q.xy *= 0.;
    if (iFrame < 1) {
        Q = vec4(0,0,0,1);
    }

    vec4 mo = D(vec2(0));
    if (length(mo.xy-mo.zw)>0.) {

       vec2 v = (mo.xy-mo.zw)/5.;
       if (length(v)>1.) v = normalize(v);
       Q.xy += exp(-.25 *ln(U,mo.xy,mo.zw))*v;
    } else {
       if (iMouse.z>0.&&length(U-iMouse.xy)< 20.) Q.w = 10.,Q.xy=vec2(0);
    }

    Q.xy *= .995;

}

Main {

Q = A(U);
vec4 dQ = vec4(0);
#define K (1./8.)
for (int x = -1; x <= 1; x++)
for (int y = -1; y <= 1; y++)
if (x!=0||y!=0) {
    vec2 v = vec2(x,y);
    vec2 W = U+v;
    vec4 q = A(W);

    vec2 a = vel(Q.xy),
         b = vel(q.xy)+v;
    float ab = dot(v,b-a);
    if (inside(W)&&abs(ab)>0.) {
        float i = dot(v,(0.5*v-a))/ab;
        float j = .5+.1*clamp(1.-q.w,0.,1.);
        float k = .5+.1*clamp(1.-Q.w,0.,1.);
        float wa = K*Q.w*min(i,j)/j;
        float wb = K*q.w*max(k+i-1.,0.)/k;
        dQ += vec4(Q.xyz,1)*wa+vec4(q.xyz,1)*wb;
    } else {
        dQ += K*Q.w*vec4(Q.xyz,1);
    }

}
if (dQ.w>0.) dQ.xyz /= dQ.w;
Q = dQ;
if (length(Q.xy)>1.) Q.xy = normalize(Q.xy);
}

Main {

Q = A(U);
vec4 O = B(U);
vec4 dQ = vec4(0);
#define K (1./8.)
for (int x = -1; x <= 1; x++)
for (int y = -1; y <= 1; y++)
if (x!=0||y!=0) {
    vec2 v = vec2(x,y);
    vec2 W = U+v;
    vec4 q = A(W);
    vec4 o = B(W);

    vec2 a = vel(Q.xy),
         b = vel(q.xy)+v;
    float ab = dot(v,b-a);
    if (inside(W)&&abs(ab)>0.) {
        float i = dot(v,(0.5*v-a))/ab;
        float j = .5+.0*clamp(1.-q.w,0.,1.);
        float k = .5+.0*clamp(1.-Q.w,0.,1.);
        float wa = K*Q.w*min(i,j)/j;
        float wb = K*q.w*max(k+i-1.,0.)/k;
        dQ += vec4(O.xyz,1)*wa+vec4(o.xyz,1)*wb;
    } else {
        dQ += K*O.w*vec4(O.xyz,1);
    }

}
if (dQ.w>0.) dQ.xyz /= dQ.w;
Q = dQ;
vec4 mo = D(U);
if (length(mo.xy-mo.zw)==0.){
    if (iMouse.z>0.&&length(U-iMouse.xy)< 20.) Q = .5 +.5*sin(iTime+vec4(1,2,3,4));
}
if (iFrame < 1) {
    Q = vec4(.9);
}


}

// keep track of mouse
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 p = texture(iChannel0,fragCoord/iResolution.xy);
    if (iMouse.z>0.) {
        if (p.z>0.) fragColor =  vec4(iMouse.xy,p.xy);
    	else fragColor =  vec4(iMouse.xy,iMouse.xy);
    }
    else fragColor = vec4(-iResolution.xy,-iResolution.xy);
}
